  /* Root */
  :root:has(.central-featured) {
    #catppuccin(@lightFlavor);

    &, body {
      color: @text;
      background-color: @base;

      .svg-Wikipedia_wordmark {
        filter: @text-filter;
      }

      .central-featured-lang small,
      .other-project-tagline,
      .site-license,
      .footer-sidebar-text {
        color: @subtext1;
      }

      .central-featured-lang :hover {
        background-color: @surface1;
      }

      .other-project-link:hover {
        background-color: @surface1;
      }

      .search-container {
        #searchInput {
          color: @text;
          background-color: @mantle;
          border-color: @overlay2;
          box-shadow: none;

          &:focus {
            border-color: @accent;
          }
        }

        .js-langpicker-label {
          color: @subtext0;
        }
        .styled-select:hover {
          background-color: @surface1;
        }
        .sprite.svg-arrow-down {
          filter: @text-filter;
        }

        button[type="submit"] {
          background-color: @accent;
          border-color: @accent;

          .svg-search-icon {
            filter: @crust-filter;
          }
        }

        .suggestions-dropdown {
          background-color: @mantle;
          border-color: @overlay2;

          .suggestion-link {
            border-bottom-color: @overlay0;

            &.active {
              background-color: fade(@accent, 10%);
            }

            .suggestion-thumbnail {
              @svg: escape(
                '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 56 56"><path fill="@{surface1}" d="M0 0h56v56H0z"/><path fill="@{overlay2}" d="M36.4 13.5H17.8v24.9c0 1.4.9 2.3 2.3 2.3h18.7v-25c.1-1.4-1-2.2-2.4-2.2M30.2 17h5.1v6.4h-5.1zm-8.8 0h6v1.8h-6zm0 4.6h6v1.8h-6zm0 15.5v-1.8h13.8v1.8zm13.8-4.5H21.4v-1.8h13.8zm0-4.7H21.4v-1.8h13.8z"/></svg>',
              );
              background-image: url("data:image/svg+xml,@{svg}");
            }

            .suggestion-title {
              color: @text;
            }
          }
        }
      }

      hr {
        border-bottom-color: @surface1;
      }

      .lang-list-border {
        background-color: @surface2;
      }
      .lang-list-button {
        background-color: @mantle;
        color: @accent;
        outline-color: @mantle;
        border-color: @surface2;

        .svg-language-icon,
        .svg-arrow-down-blue {
          filter: @accent-filter;
        }

        &:focus {
          box-shadow: inset 0 0 0 1px @accent;
        }
      }
      .lang-list-container {
        background-color: @surface0;

        .bookshelf {
          border-top-color: @overlay1;
          box-shadow: none;

          .text {
            color: @text;
            background-color: @surface2;
          }
        }
      }
    }
  }
}

/* ==UserStyle==
@name PyPI Catppuccin
@namespace github.com/catppuccin/userstyles/styles/pypi
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/pypi
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/pypi/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Apypi
@description Soothing pastel theme for PyPI
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("pypi.org") {
  @import url("https://python.catppuccin.com/pygments/catppuccin-variables.important.css");

  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();
    #lib.css-variables();

    ::selection {
      color: unset; // TODO: Basically another case of setting the text color in the selection, but unsetting it here.
    }

    body {
      background-color: @base !important;
      color: @text !important;
    }

    input,
    select,
    textarea {
      background-color: @mantle !important;
      border-color: @surface0 !important;
      color: @text !important;

      &:active,
      &:focus,
      &:hover {
        border-color: @accent !important;
        box-shadow: inset 0 0 0 1px @accent !important;
      }
    }

    input:disabled,
    select:disabled {
      &,
      &:active,
      &:focus,
      &:hover {
        background-color: @mantle !important;
      }
    }

    hr {
      background-color: @surface0 !important;
      background-image: linear-gradient(90deg, @surface0, @surface0) !important;
    }

    .project-description blockquote {
      color: @subtext0 !important;
    }

    .project-description table {
      tr {
        background-color: @base !important;
      }

      tr:nth-child(odd) {
        background-color: @mantle !important;
      }

      &, tr, th, td {
        border-color: @surface0 !important;
      }
    }

    .verified .check {
      color: @green;
    }

    .banner,
    .footer {
      background-color: @mantle !important;
      color: @text !important;
    }

    .accordion__link {
      color: @accent !important;
    }

    .password-strength {
      border-color: @surface2 !important;
    }

    .accordion__link:active,
    .accordion__link:focus {
      outline-color: @accent !important;
      outline-style: solid;
    }

    @media (max-width: 1000px) {
      .accordion__link {
        color: @text !important;
      }

      .accordion__link:active,
      .accordion__link:focus {
        outline-color: @text !important;
      }
    }

    .footer__divider {
      border-color: @surface0 !important;
    }

    a,
    .footer__menu li a {
      &:active,
      &:focus {
        outline-color: @accent !important;
      }
    }

    .skip-to-content:focus {
      background-color: @base !important;
      color: @text !important;
    }

    a:not(.site-header__logo, .button, .dropdown__link, .badge, .release__card),
    a:hover:not(
      .site-header__logo,
      .button,
      .dropdown__link,
      .badge,
      .release__card
    ),
    .footer__menu li a,
    .footer__text a,
    .language-switcher ul button {
      color: @accent !important;
    }

    .footer__text a,
    .language-switcher ul button {
      &:active,
      &:focus {
        outline-color: @accent !important;
      }
    }

    .footer__menu h2,
    .footer__text,
    .language-switcher ul button.language-switcher__selected::before {
      color: @text !important;
    }

    .language-switcher {
      background-color: @crust !important;
      border-top-color: @crust !important;
      color: @text !important;
    }

    .horizontal-section--grey {
      background-color: @base !important;
      border-bottom-color: @base !important;
      border-top-color: @base !important;
    }

    .statistics-bar__statistic {
      color: @subtext0 !important;
    }

    .lede-paragraph {
      color: @subtext1 !important;
    }

    .site-header {
      background-color: @crust !important;
